mapper.select:
    select
    
    from sensor_inclination
    
where create_time <=#{endTime}

    and create_time >=#{startTime}

    select
    
    from sensor_inclination
    where angle_total = (
    select
    max(angle_total)
    from sensor_inclination
    
  where create_time <=#{endTime}
  
    and create_time >=#{startTime}
    and sensor_id = #{sensorId}
    )
    
and create_time <=#{endTime}

    and create_time >=#{startTime}
    and sensor_id = #{sensorId}
    limit 1
